try {
  const startTime = Date.now();
  console.log('üöÄ TGB PAYMENT VERIFICATION STARTED', { timestamp: new Date().toISOString() });

  // ===============================================
  // 1. DATA EXTRACTION & VALIDATION
  // ===============================================

  const aiResult = $input.first().json || {};
  const webhookData = $('Process Webhook Data Node').item?.json || {};

  console.log('üì• INPUT DATA RECEIVED', {
    webhook_keys: Object.keys(webhookData),
    ai_result_available: !!aiResult.content,
    ai_content_length: aiResult.content?.length || 0
  });

  // Enhanced ORDER_ID extraction with multiple fallbacks
  const extractOrderID = () => {
    const sources = [
      webhookData.ORDER_ID,
      webhookData.order_id,
      $('Process Webhook Data Node').item?.json?.ORDER_ID,
      $('Webhook').item?.json?.order_id,
      $('Webhook').item?.json?.ORDER_ID
    ];

    console.log('üîç ORDER_ID EXTRACTION SOURCES:', sources);

    const orderID = sources.find(id => id && id !== 'UNKNOWN' && id !== '') || 'AUTO_' + Date.now().toString().slice(-8);

    if (orderID.startsWith('AUTO_')) {
      console.log('‚ö†Ô∏è  AUTO-GENERATED ORDER_ID:', orderID);
    }

    return orderID;
  };

  const extractCustomerEmail = () => {
    const sources = [
      webhookData.CUSTOMER_EMAIL,
      webhookData.customer_email,
      webhookData.EMAIL,
      webhookData.email
    ];

    return sources.find(email => email && email.includes('@')) || 'unknown@customer.com';
  };

  const orderID = extractOrderID();
  const customerEmail = extractCustomerEmail();

  console.log('‚úÖ EXTRACTED IDENTIFIERS', {
    order_id: orderID,
    customer_email: customerEmail,
    extraction_method: orderID.startsWith('AUTO_') ? 'auto_generated' : 'from_webhook'
  });

  // ===============================================
  // 2. AI PAYMENT ANALYSIS PROCESSING
  // ===============================================

  let paymentData = {
    transaction_id: 'MANUAL_UPLOAD_' + Date.now().toString().slice(-6),
    amount: 0,
    recipient_name: 'TGB',
    sender_name: 'Customer',
    payment_method: 'Manual Upload',
    transaction_datetime: new Date().toLocaleString('en-PH', { timeZone: 'Asia/Manila' }),
    status: 'PENDING',
    confidence_score: 75,
    recommended_action: 'AUTO_APPROVE'
  };

  let aiProcessingSuccess = false;

  try {
    if (aiResult.content) {
      console.log('ü§ñ PROCESSING AI ANALYSIS...');

      // Clean and parse AI response
      const cleanContent = aiResult.content
        .replace(/```json\n?/g, '')
        .replace(/\n?```/g, '')
        .replace(/^[^{]*/g, '') // Remove any text before first {
        .replace(/[^}]*$/g, '') // Remove any text after last }
        .trim();

      console.log('üßπ CLEANED AI CONTENT:', cleanContent.substring(0, 200) + '...');

      const parsed = JSON.parse(cleanContent);
      paymentData = { ...paymentData, ...parsed };
      aiProcessingSuccess = true;

      console.log('‚úÖ AI ANALYSIS SUCCESS', {
        transaction_id: paymentData.transaction_id,
        amount: paymentData.amount,
        payment_method: paymentData.payment_method,
        confidence: paymentData.confidence_score
      });

    } else {
      console.log('‚ö†Ô∏è  NO AI CONTENT - USING MANUAL DEFAULTS');
    }
  } catch (aiError) {
    console.log('‚ö†Ô∏è  AI PARSING FAILED - USING DEFAULTS', {
      error: aiError.message,
      ai_content_preview: aiResult.content?.substring(0, 100)
    });
  }

  // ===============================================
  // 3. BUSINESS LOGIC & STATUS DETERMINATION
  // ===============================================

  const phtNow = new Date().toLocaleString('en-PH', { timeZone: 'Asia/Manila' });
  const amount = parseFloat(paymentData.amount) || 0;
  const confidence = Math.max(75, parseInt(paymentData.confidence_score) || 75); // Minimum 75% for testing

  // Enhanced business logic
  const determinePaymentStatus = (confidence, amount, aiSuccess) => {
    // TGB Business Rules
    if (confidence >= 90 && amount >= 500 && amount <= 100000 && aiSuccess) {
      return { status: 'APPROVED', verification: 'AUTO_APPROVED', action: 'AUTO_APPROVE' };
    } else if (confidence >= 75 && amount >= 100) {
      return { status: 'PROCESSING', verification: 'PENDING_REVIEW', action: 'MANUAL_REVIEW_4HR' };
    } else if (!aiSuccess || amount === 0) {
      return { status: 'PROCESSING', verification: 'MANUAL_UPLOAD', action: 'MANUAL_REVIEW_4HR' };
    } else {
      return { status: 'UNDER_REVIEW', verification: 'NEEDS_CLARIFICATION', action: 'CUSTOMER_CLARIFICATION' };
    }
  };

  const businessDecision = determinePaymentStatus(confidence, amount, aiProcessingSuccess);
  const newPaymentStatus = businessDecision.status;
  const verificationStatus = businessDecision.verification;
  const recommendedAction = businessDecision.action;

  console.log('üìä BUSINESS DECISION', {
    payment_status: newPaymentStatus,
    verification_status: verificationStatus,
    recommended_action: recommendedAction,
    confidence_score: confidence,
    amount: amount,
    ai_success: aiProcessingSuccess
  });

  // ===============================================
  // 4. FRAUD DETECTION & RISK ASSESSMENT
  // ===============================================

  const fraudFlags = [];
  const riskFactors = [];

  // Risk assessment
  if (amount > 50000) {
    fraudFlags.push('High Amount (>‚Ç±50K)');
    riskFactors.push('large_transaction');
  }
  if (amount < 100 && amount > 0) {
    fraudFlags.push('Unusually Low Amount');
    riskFactors.push('micro_transaction');
  }
  if (confidence < 70) {
    fraudFlags.push('Low AI Confidence');
    riskFactors.push('low_confidence');
  }
  if (!aiProcessingSuccess) {
    fraudFlags.push('Manual Upload');
    riskFactors.push('manual_processing');
  }

  const riskLevel = riskFactors.length === 0 ? 'LOW' : 
                   riskFactors.length <= 2 ? 'MEDIUM' : 'HIGH';

  console.log('üõ°Ô∏è  FRAUD ASSESSMENT', {
    risk_level: riskLevel,
    fraud_flags: fraudFlags,
    risk_factors: riskFactors
  });

  // ===============================================
  // 5. NEXT STEPS DETERMINATION
  // ===============================================

  const getNextSteps = (status, verification) => {
    const stepMap = {
      'APPROVED': [
        'Payment verified and approved',
        'Order will be prepared for shipping',
        'You will receive tracking information soon',
        'Expected processing: 24-48 hours'
      ],
      'PROCESSING': [
        'Payment received and under review',
        'TGB admins will verify within 4 hours',
        'Email confirmation will be sent upon approval',
        'Contact: 0946-004-3002 for urgent matters'
      ],
      'UNDER_REVIEW': [
        'Additional verification required',
        'TGB admin may contact you for clarification',
        'Please ensure payment details are clear',
        'Review typically completed within 24 hours'
      ]
    };

    return stepMap[status] || ['Please contact TGB admins for assistance'];
  };

  const nextSteps = getNextSteps(newPaymentStatus, verificationStatus);

  // ===============================================
  // 6. DATA VALIDATION
  // ===============================================

  const validateDataFlow = (data) => {
    const required = ['ORDER_ID', 'PAYMENT_STATUS', 'UPDATED_AT'];
    const missing = required.filter(field => !data[field] || data[field] === '');

    if (missing.length > 0) {
      throw new Error(`‚ùå VALIDATION FAILED - Missing required fields: ${missing.join(', ')}`);
    }

    // Additional validation
    if (!data.ORDER_ID.match(/^(GB|AUTO_)\d+/)) {
      console.log('‚ö†Ô∏è  ORDER_ID format warning:', data.ORDER_ID);
    }

    console.log('‚úÖ DATA VALIDATION PASSED');
    return true;
  };

  // ===============================================
  // 7. RESPONSE STRUCTURES
  // ===============================================

  const processingTime = Date.now() - startTime;
  const transactionHash = btoa(orderID + phtNow + processingTime).slice(0, 12);

  // Enhanced React frontend response
  const reactResponse = {
    success: true,
    order_id: orderID,
    verification_status: verificationStatus,
    payment_status: newPaymentStatus,
    message: `Payment verification completed for Order ${orderID}`,
    status_description: `Status: ${newPaymentStatus} - ${verificationStatus}`,
    processed_at: phtNow,
    metadata: {
      processing_time_ms: processingTime,
      ai_confidence: confidence,
      transaction_id: transactionHash,
      risk_level: riskLevel,
      next_steps: nextSteps,
      contact_info: {
        phone: '0946-004-3002',
        email: 'legerynpia@soleblessing.com',
        business: 'THE GOLD BLESSING - Amadeo, Cavite'
      }
    },
    payment_details: {
      transaction_id: paymentData.transaction_id,
      amount: amount.toFixed(2),
      payment_method: paymentData.payment_method,
      recipient: paymentData.recipient_name,
      sender: paymentData.sender_name,
      datetime: paymentData.transaction_datetime
    }
  };

  // Google Sheets update data
  const sheetsUpdateData = {
    ORDER_ID: orderID,
    PAYMENT_STATUS: newPaymentStatus,
    UPDATED_AT: phtNow,
    VERIFICATION_STATUS: verificationStatus,
    AI_CONFIDENCE: confidence.toString(),
    PROCESSED_BY: 'Payment_Portal_Enhanced',
    TRANSACTION_HASH: transactionHash,
    RISK_LEVEL: riskLevel,
    FRAUD_FLAGS: fraudFlags.join('; ') || 'None',
    PROCESSING_TIME_MS: processingTime.toString(),
    AI_SUCCESS: aiProcessingSuccess ? 'YES' : 'NO',
    AMOUNT_DETECTED: amount.toFixed(2),
    PAYMENT_METHOD: paymentData.payment_method,
    RECOMMENDED_ACTION: recommendedAction
  };

  // Validate before sending
  validateDataFlow(sheetsUpdateData);

  console.log('üì§ FINAL OUTPUT PREPARED', {
    order_id: orderID,
    payment_status: newPaymentStatus,
    verification_status: verificationStatus,
    processing_time_ms: processingTime,
    transaction_hash: transactionHash,
