  // ===============================================
  // COMPREHENSIVE ERROR HANDLING
  // ===============================================

  const errorTimestamp = new Date().toLocaleString('en-PH', { timeZone: 'Asia/Manila' });
  const errorId = 'ERR_' + Date.now().toString().slice(-8);

